@link-blue: #428bca;
@link-color-hover:  darken(@link-color, 10%);
@light-blue: @link-blue +  #111111;
// Usage
a, link {
    color: @link-blue;
}
a:hover {
    color: @link-color-hover;
}
.header {
    color: @light-blue;
}

a, link {
    color: #428bca;
}
a:hover {
    color: #36a1d2;
}
.header {
    color: #539cda;
}

// Variables
@images: "../img";
// Usage
body {
    color: #444444;
    background: url("@{images}/white-sand.png");
}

.block {
    color: red;
    color: blue;
}
.block .content {
    background-color: blue;
}

a {
    color: blue;
}
a:hover {
    color: green
}

.button {
    &-ok {
        background-image: url("ok.png");
    }
    &-cancel {
        background-image: url("cancel.png");
    }
    &-custom {
        background-image: url("custom.png");
    }
}

.button-ok {
    background-image: url("ok.png");
}
.button-cancel {
    background-image: url("cancel.png");
}
.button-custom {
    background-image: url("custom.png");
}

.link {
  & + & {
    color: red;
  }
  & & {
    color: green;
  }
  && {
    color: blue;
  }
  &, &ish {
    color: cyan;
  }
}

.link + .link {
    color: red;
}
.link .link {
    color: green;
}
.link.link {
    color: blue;
}
.link, .linkish {
    color: cyan;
}

.grand {
  .parent {
    & > & {
      color: red;
    }
    & & {
      color: green;
    }
    && {
      color: blue;
    }
    &, &ish {
      color: cyan;
    }
  }
}

.grand .parent > .grand .parent {
  color: red;
}
.grand .parent .grand .parent {
  color: green;
}
.grand .parent.grand .parent {
  color: blue;
}
.grand .parent, .grand .parentish {
  color: cyan;
}

p, a, ul, li {
  border-top: 2px dotted #366;
  & + & {
    border-top: 0;
  }
}

.c:extend(.d all) {
  // 继承所有包含.d 类的样式，如 ".x.d" 和 ".d.x"
}
.c:extend(.d) {
  // 只继承 .d 类的样式
}

nav ul {
  &:extend(.inline);
  background: blue;
}
.inline {
  color: red;
}

nav ul {
  background: blue;
}
.inline,
nav ul {
  color: red;
}

.mixin() {   // 加入圆括号的类不会被编译到css文件中
  box-shadow+: inset 0 0 10px #555;
}
.myclass {
  .mixin();
  box-shadow+: 0 0 20px black;
}

.a, #b {
  color: red;
}
.mixin-class {
  color: red;
}
.mixin-id {
  color: red;
}

.my-mixin {
  color: black;
}
.class {
  color: black;
  background: white;
}

// 生成器
.wrap() {
  text-wrap: wrap;
  white-space: -moz-pre-wrap;
  white-space: pre-wrap;
  word-wrap: break-word;
}
// Usage
pre { .wrap }

pre {
  text-wrap: wrap;
  white-space: -moz-pre-wrap;
  white-space: pre-wrap;
  word-wrap: break-word;
}

.class1 {
  color: #33acfe;
  margin: 20px;
  padding: 20px;
}
.class2 {
  color: #efca44;
  margin: 10px;
  padding: 40px;
}

.average(@x, @y) {
  @average: ((@x + @y) / 2);
}
div {
  .average(16px, 50px); // "call" the mixin
  padding: @average;    // use its "return" value
}

.mixin (@a) when (lightness(@a) >= 50%) {
  background-color: black;
}
.mixin (@a) when (lightness(@a) < 50%) {
  background-color: white;
}
.mixin (@a) {
  color: @a;
}
// Usage
.class1 { .mixin(#ddd) }
.class2 { .mixin(#555) }

.class1 {
  background-color: black;
  color: #ddd;
}
.class2 {
  background-color: white;
  color: #555;
}

@media: mobile;
.mixin (@a) when (@media = mobile) { width: @a }
.mixin (@a) when (@media = desktop) { width: @b }
.max (@a; @b) when (@a > @b) { width: @a }
.max (@a; @b) when (@a < @b) { width: @b }

& when (@my-option = true) {
  button {
    color: white;
  }
  a {
    color: blue;
  }
}

